hudson.tasks.Shell.command:
# Setup environment for builds

cd /home/ci/work/${TRIGGER_PARAM_PLATFORM}
if [ ! -d "${TRIGGER_PARAM_INSTALL_FOLDER}" ]; then
    cp -r ${TRIGGER_PARAM_BASE_INSTALL_FOLDER} ${TRIGGER_PARAM_INSTALL_FOLDER}
fi

#!/bin/bash
# Build libweintek echo "================================ libweintek ================================ libweintek ================================ libweintek ================================"
export LIBWEINTEK_PATH=${WORKSPACE}/libweintek export INSTALL_PREFIX=/home/ci/work/${TRIGGER_PARAM_PLATFORM}/${TRIGGER_PARAM_INSTALL_FOLDER}
if [ "${TRIGGER_PARAM_PLATFORM}" = "cMT3000" ]; then export CROSS_COMPILER_TOOLCHAIN=/home/ci/work/cMT3000/target/environment-setup-armv7ahf-vfp-neon-weintek-linux-gnueabi elif [ "${TRIGGER_PARAM_PLATFORM}" = "cMT-iv5" ]; then export CROSS_COMPILER_TOOLCHAIN=/home/ci/work/cMT-iv5/target/environment-setup-armv7ahf-vfp-neon-weintek-linux-gnueabi fi
cd ${LIBWEINTEK_PATH}/build if [ "${TRIGGER_PARAM_LIBWEINTEK_BUILD_POLICY}" = "brandnew" ]; then rm -rf jenkins/${TRIGGER_PARAM_PLATFORM} fi if [ ! -d "jenkins" ]; then mkdir -p jenkins fi if [ ! -d "jenkins/${TRIGGER_PARAM_PLATFORM}" ]; then mkdir -p jenkins/${TRIGGER_PARAM_PLATFORM} fi cd jenkins/${TRIGGER_PARAM_PLATFORM}
${LIBWEINTEK_PATH}/build/${TRIGGER_PARAM_PLATFORM}/cmake_libweintek.sh ${LIBWEINTEK_PATH} ${INSTALL_PREFIX} ${CROSS_COMPILER_TOOLCHAIN} RelWithDebInfo
if [ "${TRIGGER_PARAM_LIBWEINTEK_BUILD_POLICY}" = "clean" ]; then make clean fi make install VERBOSE=1
