hudson.tasks.Shell.command:
# Build libweintek echo "================================ libweintek ================================ libweintek ================================ libweintek ================================"
export LIBWEINTEK_PATH=${WORKSPACE}/libweintek export INSTALL_PREFIX=/home/ci/work/${TRIGGER_PARAM_PLATFORM}/${TRIGGER_PARAM_INSTALL_FOLDER} export BUILD_TYPE=RelWithDebInfo
if [ "${TRIGGER_PARAM_PLATFORM}" = "cMT3000" ]; then export CROSS_COMPILER_TOOLCHAIN=/home/ci/work/cMT3000/target/environment-setup-armv7ahf-vfp-neon-weintek-linux-gnueabi elif [ "${TRIGGER_PARAM_PLATFORM}" = "cMT-iv5" ]; then export CROSS_COMPILER_TOOLCHAIN=/home/ci/work/cMT-iv5/target/environment-setup-armv7ahf-vfp-neon-weintek-linux-gnueabi fi
cd ${WORKSPACE}/libweintek/build if [ "${TRIGGER_PARAM_LIBWEINTEK_BUILD_POLICY}" = "brandnew" ]; then rm -rf jenkins/${TRIGGER_PARAM_PLATFORM} fi mkdir -p jenkins/${TRIGGER_PARAM_PLATFORM} cd jenkins/${TRIGGER_PARAM_PLATFORM}
${WORKSPACE}/libweintek/build/${TRIGGER_PARAM_PLATFORM}/cmake_libweintek.sh ${LIBWEINTEK_PATH} ${INSTALL_PREFIX} ${CROSS_COMPILER_TOOLCHAIN} ${BUILD_TYPE}
if [ "${TRIGGER_PARAM_LIBWEINTEK_BUILD_POLICY}" = "clean" ]; then make clean fi make install VERBOSE=1

buildStep.command:
#!/bin/bash
# Build gui echo "================================ gui ================================ gui ================================ gui ================================"
export GUI_PATH=${WORKSPACE}/gui export DSERVER_PATH=${WORKSPACE}/dserver export LIBWEINTEK_PATH=${WORKSPACE}/libweintek export INSTALL_PREFIX=/home/ci/work/${TRIGGER_PARAM_PLATFORM}/${TRIGGER_PARAM_INSTALL_FOLDER} export BUILD_TYPE=RelWithDebInfo
if [ "${TRIGGER_PARAM_PLATFORM}" = "cMT3000" ]; then export CROSS_COMPILER_TOOLCHAIN=/home/ci/work/cMT3000/target/environment-setup-armv7ahf-vfp-neon-weintek-linux-gnueabi elif [ "${TRIGGER_PARAM_PLATFORM}" = "cMT-iv5" ]; then export CROSS_COMPILER_TOOLCHAIN=/home/ci/work/cMT-iv5/target/environment-setup-armv7ahf-vfp-neon-weintek-linux-gnueabi fi
if [ "${TRIGGER_PARAM_PLATFORM}" = "Ubuntu" ]; then export PATH=/opt/Qt5.4.2/5.4/gcc_64/bin:${PATH} fi
cd ${WORKSPACE}/gui/build if [ "${TRIGGER_PARAM_GUI_BUILD_POLICY}" = "brandnew" ]; then rm -rf jenkins/${TRIGGER_PARAM_PLATFORM} fi if [ ! -d "jenkins" ]; then mkdir -p jenkins fi if [ ! -d "jenkins/${TRIGGER_PARAM_PLATFORM}" ]; then mkdir -p jenkins/${TRIGGER_PARAM_PLATFORM} fi cd jenkins/${TRIGGER_PARAM_PLATFORM}
cp ${WORKSPACE}/gui/build/${TRIGGER_PARAM_PLATFORM}/features.pri . ${WORKSPACE}/gui/build/${TRIGGER_PARAM_PLATFORM}/qmake_gui.sh ${GUI_PATH} ${LIBWEINTEK_PATH} ${DSERVER_PATH} ${INSTALL_PREFIX} ${CROSS_COMPILER_TOOLCHAIN} ${BUILD_TYPE}
if test -n "${CROSS_COMPILER_TOOLCHAIN}"; then source ${CROSS_COMPILER_TOOLCHAIN} fi
if [ "${TRIGGER_PARAM_GUI_BUILD_POLICY}" = "clean" ]; then make clean fi make
if [ "${TRIGGER_PARAM_PLATFORM}" = "cMT3000" ]; then cp ${INSTALL_PREFIX}/bin/gui ${WORKSPACE}/artifacts/gui.RelWithDebInfo strip ${INSTALL_PREFIX}/bin/gui test_mtcrypt --encrypt ${INSTALL_PREFIX}/bin/gui --out ${INSTALL_PREFIX}/bin/gui.c30 cp ${INSTALL_PREFIX}/bin/gui.c30 ${WORKSPACE}/artifacts elif [ "${TRIGGER_PARAM_PLATFORM}" = "Ubuntu" ]; then cp ${INSTALL_PREFIX}/bin/gui ${WORKSPACE}/artifacts/gui.RelWithDebInfo strip ${INSTALL_PREFIX}/bin/gui cp ${INSTALL_PREFIX}/bin/gui ${WORKSPACE}/artifacts fi
