buildStep.command:
#!/bin/bash

export QT_CHARTS_PATH=/home/clydeliu/projects/3rdParty/QtCharts/${MATRIX_AXIS_QT_CHARTS_VERSION}

rm -rf QtCharts
mkdir QtCharts

cp -r ${QT_CHARTS_PATH} QtCharts

hudson.tasks.Shell.command:
#!/bin/bash

case $MATRIX_AXIS_PLATFORM in
    "cMT3000")       export CROSS_COMPILER_TOOLCHAIN=/home/ci/work/cMT3000/target/environment-setup-armv7ahf-vfp-neon-weintek-linux-gnueabi  ;;
    "cMT-iv5")       export CROSS_COMPILER_TOOLCHAIN=/home/ci/work/cMT-iv5/target/environment-setup-armv7ahf-vfp-neon-weintek-linux-gnueabi  ;;
esac

case $MATRIX_AXIS_PLATFORM in
    "android-arm")   export PATH=/opt/Qt5.3.2/5.3/android_armv7/bin:${PATH}  ;;
    "android-x86")   export PATH=/opt/Qt5.3.2/5.3/android_x86/bin:${PATH}    ;;
    "Ubuntu")        export PATH=/opt/Qt5.4.2/5.4/gcc_64/bin:${PATH}         ;;
esac

case $MATRIX_AXIS_PLATFORM in
    "cMT-iv5")       export CHARTS_PROJECT_NAME=charts_no_opengl.pro  ;;
    "android-arm")   export CHARTS_PROJECT_NAME=charts_no_opengl.pro  ;;
    "android-x86")   export CHARTS_PROJECT_NAME=charts_no_opengl.pro  ;;
    *)               export CHARTS_PROJECT_NAME=charts.pro            ;;
esac

if [ "${MATRIX_AXIS_PLATFORM}" = "android-arm" ] || [ "${MATRIX_AXIS_PLATFORM}" = "android-x86" ]; then
	export ANDROID_NDK_ROOT=/home/ci/Android/android-ndk
fi

rm -rf ${WORKSPACE}/QtCharts/${MATRIX_AXIS_QT_CHARTS_VERSION}/Src/lib/*
rm -rf ${WORKSPACE}/artifacts
mkdir ${WORKSPACE}/artifacts

cd ${WORKSPACE}/QtCharts/${MATRIX_AXIS_QT_CHARTS_VERSION}/Src/src/charts

if test -n "${CROSS_COMPILER_TOOLCHAIN}"; then
    source ${CROSS_COMPILER_TOOLCHAIN}
fi

qmake ${CHARTS_PROJECT_NAME}
make

cd ${WORKSPACE}/QtCharts/${MATRIX_AXIS_QT_CHARTS_VERSION}/Src/lib

if [ "${CHARTS_PROJECT_NAME}" = "charts_no_opengl.pro" ]; then
    mv libQt2Charts.a libQt5Charts.a
fi
cp libQt5Charts.a ${WORKSPACE}/artifacts
